import "./Home.css";

import logo from "../../assets/Logo.svg";
import footer from "./footerObject";

import { Image, Row, Col, Container, Form, Button } from "react-bootstrap";
import axios from "axios";

import React, { useState } from "react";

function Home() {
	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const url = "http://localhost:8000/";
	const uploadUser = async (data) => {
		await axios.post(url, data).then((res) => {
			console.log(res.data);
		});
	};

	const handleOnChangeName = (e) => {
		setName(e.target.value);
	};
	const handleOnChangeEmail = (e) => {
		setEmail(e.target.value);
	};
	return (
		<Container fluid>
			<Row xl={1} lg={1} md={1} sm={1} xs={1}>
				<Col className="text-center">
					<Image fluid src={logo} className="mt-5 mb-5 logo"></Image>
				</Col>
				<Col className="back-site text-center m-0">
					<h2 className="title mt-3">
						Nuestra Web está <br></br> en proceso...
					</h2>
					<p className="p-body">
						Pero quédate cerca, el lanzamiento será pronto.<br></br>
						Deja tus datos y te avisamos!
					</p>

					<Row>
						<Col></Col>
						<Col className="w-75">
							<Form>
								<Form.Row>
									<Col>
										<Form.Control
											required
											type="text"
											placeholder="(Nombre)"
											className="input"
											onChange={handleOnChangeName}
										></Form.Control>
									</Col>
									<Col>
										<Form.Control
											required
											type="email"
											placeholder="(Email)"
											className="input"
											onChange={handleOnChangeEmail}
										></Form.Control>
									</Col>
								</Form.Row>
								<Button
									className="button mt-4"
									type="button"
									onClick={async () => {
										const user = {
											name: name,
											email: email,
										};
										await uploadUser(user);
									}}
								>
									Enviar
								</Button>
							</Form>
						</Col>
						<Col></Col>
					</Row>

					<Row className="mt-4 mb-4">
						<Col></Col>
						<Col xl={6}>
							{footer.map((element) => {
								return (
									<>
										<Image
											fluid
											src={instagram}
											className="w-10 "
										></Image>
										<a
											href=""
											className="link ml-3 mr-5 font-italic"
										>
											@guadalupe_estudio
										</a>
									</>
								);
							})}
						</Col>
						<Col></Col>
					</Row>
				</Col>
			</Row>
		</Container>
	);
}

export default Home;
